mapper.select:

select

from marriage_user
        where 1 = 1

    and user_id = #{userId,jdbcType=INTEGER}


    and user_name like concat('%', concat(#{userName,jdbcType=VARCHAR}, '%'))


    and wechat_id != #{weChatId,jdbcType=VARCHAR}


    and nick_name like concat('%', concat(#{nickName,jdbcType=VARCHAR}, '%'))


    and native_place like concat('%', concat(#{nativePlace,jdbcType=VARCHAR}, '%'))


    and current_location like concat('%', concat(#{currentLocation,jdbcType=VARCHAR}, '%'))


    and gender = #{gender,jdbcType=TINYINT}


    and birth_date between #{birthDateStart,jdbcType=VARCHAR} and #{birthDateEnd,jdbcType=VARCHAR}


    and blood_type = #{bloodType,jdbcType=TINYINT}


    and height between #{heightStart,jdbcType=TINYINT} and #{heightEnd,jdbcType=TINYINT}


    and weight between #{weightStart,jdbcType=TINYINT} and #{weightEnd,jdbcType=TINYINT}


    and education = #{education,jdbcType=TINYINT}


    and house = #{house,jdbcType=TINYINT}


    and job_info like concat('%', concat(#{jobInfo,jdbcType=VARCHAR}, '%'))


    and interest like concat('%', concat(#{interest,jdbcType=VARCHAR}, '%'))


    and marriage_status = #{marriageStatus,jdbcType=TINYINT}


    and family like concat('%', concat(#{family,jdbcType=VARCHAR}, '%'))


    and target like concat('%', concat(#{target,jdbcType=VARCHAR}, '%'))


    and declaration like concat('%', concat(#{declaration,jdbcType=VARCHAR}, '%'))

select count(1)
from sys_manage
where manage_wx_id = #{weChatId}
  and is_valid = 0;

select user_status
from marriage_user
where wechat_id = #{weChatId}

mapper.update:

update marriage_user
set user_status = #{userStatus,jdbcType=TINYINT}
where user_id = #{userId,jdbcType=INTEGER}
